//100-return=测量值  这个测量值是正数，实际要加负号
  float PT100_ResToTemp_L(float resistance) {
    int low = 0;
    int mid = 0;
    int high = 1000;
    if ((resistance < pt100_table_L[0]) || (resistance > pt100_table_L[high])) {
      return -1; // 超限，不做特殊处理
    }
    while (high >= low) {
      mid = (high + low) / 2;
      if (resistance >= pt100_table_L[mid]) {
        if (resistance < pt100_table_L[mid + 1]) {
          return (0.1 * mid + (resistance - pt100_table_L[mid]) * 0.1 / ((pt100_table_L[mid + 1]) - pt100_table_L[mid]));
        } else {
          low = mid + 1;
        }
      } else if (resistance < pt100_table_L[mid]) {
        high = mid - 1;
      }
    }
    return -1; // 查找失败，不做特殊处理
  };

